    publish: function() {

        try {
            var inputs = {};
            inputs['calendar_id'] = this.calendarID; // String 
            inputs['summary'] = "On This Day (" + this.year + ")"; // String (Full UTF-8) 
            inputs['description'] = this.pageRec.getValue('output'); // String (Full UTF-8) 
            inputs['all_day'] = true; // True/False 
            inputs['start_time'] = this.otd; // Date/Time 
            inputs['recurrence'] = {
                "RRULE": "FREQ=YEARLY",
                "RDATE": "",
                "EXDATE": ""
            }; // Object 
            inputs['attendees'] = []; // Array.Object 

            // Execute Synchronously: Run in foreground. Code snippet has access to outputs.
            var result = sn_fd.FlowAPI.getRunner().action('sn_gcalendar_spoke.create_event_on_calendar').inForeground().withInputs(inputs).run();
            var outputs = result.getOutputs();

            // Get Outputs:
            // Note: outputs can only be retrieved when executing synchronously.
            var status = outputs['status']; // Choice
            var error_message = outputs['error_message']; // String
            var event = outputs['event']; // Object

        } catch (ex) {
            var message = ex.getMessage();
            gs.error(message);
        }

    },

    type: 'GoogleCalendar'
};
